    private function cat_add($id_new = 0) {

        $query = 'SELECT max(`id_cat`) AS total FROM `' . _DB_PREFIX_ . 'news_cats`  ; ';
        $rs = Db::getInstance(_PS_USE_SQL_SLAVE_)->ExecuteS($query);

