        }

        echo '<script> parent.news_ajax("newsCatContent","?tab=AdminModules&configure=' . $this->name . '&token=' . $_REQUEST['token'] . '&news_ajax=1&news_list_cat=1&id_news=' . $id_new . '");</script>';

